con:config.script:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context );
def propertyList = testRunner.testCase.getTestStepByName( "Properties" );
def dumpDir = propertyList.getPropertyValue( "SUT.HIEM.FTA.dumpDir" );
def copyFile = propertyList.getPropertyValue( "SUT.HIEM.FTA.copyFile" );
def fileInDumpDir = dumpDir + "/file.txt";

log.info copyFile;
File myFile = new File(copyFile);
log.info dumpDir;
if(myFile.exists())
{
    log.info copyFile;
    log.info fileInDumpDir;
    new File(fileInDumpDir).withWriter 
    { 
        file -> new File(copyFile).eachLine 
        { 
            line -> file.writeLine(line)  
        }
    }
}

